import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import { toast } from 'react-hot-toast';
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
  PieChart,
  Pie,
  Cell,
  LineChart,
  Line,
} from 'recharts';

const Charts = () => {
  const { user, token } = useAuth();
  const [salesData, setSalesData] = useState([]);
  const [categoryData, setCategoryData] = useState([]);
  const [monthlyData, setMonthlyData] = useState([]);
  const [loading, setLoading] = useState(true);

  const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042', '#8884D8'];

  useEffect(() => {
    fetchAnalyticsData();
  }, []);

  const fetchAnalyticsData = async () => {
    try {
      // Fetch sales by book data
      const salesResponse = await fetch('/api/analytics/sales-by-book', {
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      });

      if (salesResponse.ok) {
        const salesResult = await salesResponse.json();
        setSalesData(salesResult);
      }

      // Fetch books by category data
      const categoryResponse = await fetch('/api/analytics/books-by-category', {
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      });

      if (categoryResponse.ok) {
        const categoryResult = await categoryResponse.json();
        setCategoryData(categoryResult);
      }

      // Fetch monthly revenue data
      const monthlyResponse = await fetch('/api/analytics/monthly-revenue', {
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      });

      if (monthlyResponse.ok) {
        const monthlyResult = await monthlyResponse.json();
        setMonthlyData(monthlyResult);
      }

    } catch (error) {
      console.error('Error fetching analytics data:', error);
      toast.error('Failed to load analytics data');
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-primary-500"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div>
        <h1 className="text-3xl font-bold text-gray-900">Analytics Dashboard</h1>
        <p className="mt-2 text-gray-600">
          Comprehensive analytics and data visualization
        </p>
      </div>

      {/* Sales by Book Chart */}
      <div className="card">
        <div className="card-header">
          <h3 className="text-lg font-medium text-gray-900">Sales by Book</h3>
          <p className="text-sm text-gray-500">Top selling books based on quantity sold</p>
        </div>
        <div className="card-body">
          <ResponsiveContainer width="100%" height={300}>
            <BarChart data={salesData}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis 
                dataKey="title" 
                angle={-45}
                textAnchor="end"
                height={100}
                interval={0}
              />
              <YAxis />
              <Tooltip />
              <Legend />
              <Bar dataKey="total_sold" fill="#3B82F6" />
            </BarChart>
          </ResponsiveContainer>
        </div>
      </div>

      {/* Books by Category Chart */}
      <div className="card">
        <div className="card-header">
          <h3 className="text-lg font-medium text-gray-900">Books by Category</h3>
          <p className="text-sm text-gray-500">Distribution of books across different categories</p>
        </div>
        <div className="card-body">
          <ResponsiveContainer width="100%" height={300}>
            <PieChart>
              <Pie
                data={categoryData}
                cx="50%"
                cy="50%"
                labelLine={false}
                label={({ name, percent }) => `${name} ${(percent * 100).toFixed(0)}%`}
                outerRadius={80}
                fill="#8884d8"
                dataKey="book_count"
              >
                {categoryData.map((entry, index) => (
                  <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                ))}
              </Pie>
              <Tooltip />
            </PieChart>
          </ResponsiveContainer>
        </div>
      </div>

      {/* Monthly Revenue Chart */}
      <div className="card">
        <div className="card-header">
          <h3 className="text-lg font-medium text-gray-900">Monthly Revenue Trend</h3>
          <p className="text-sm text-gray-500">Revenue trends over the past months</p>
        </div>
        <div className="card-body">
          <ResponsiveContainer width="100%" height={300}>
            <LineChart data={monthlyData}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="month" />
              <YAxis />
              <Tooltip />
              <Legend />
              <Line 
                type="monotone" 
                dataKey="revenue" 
                stroke="#10B981" 
                strokeWidth={2}
              />
            </LineChart>
          </ResponsiveContainer>
        </div>
      </div>

      {/* Summary Statistics */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <div className="card">
          <div className="card-body text-center">
            <div className="text-2xl font-bold text-blue-600">
              {salesData.reduce((sum, item) => sum + parseInt(item.total_sold || 0), 0)}
            </div>
            <div className="text-gray-600">Total Books Sold</div>
          </div>
        </div>

        <div className="card">
          <div className="card-body text-center">
            <div className="text-2xl font-bold text-green-600">
              {categoryData.reduce((sum, item) => sum + parseInt(item.book_count || 0), 0)}
            </div>
            <div className="text-gray-600">Total Books</div>
          </div>
        </div>

        <div className="card">
          <div className="card-body text-center">
            <div className="text-2xl font-bold text-purple-600">
              ${monthlyData.reduce((sum, item) => sum + parseFloat(item.revenue || 0), 0).toFixed(2)}
            </div>
            <div className="text-gray-600">Total Revenue</div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Charts;
