# Online Bookstore Setup Script\n# CS 665 Database Project\n\nWrite-Host \"================================\" -ForegroundColor Cyan\nWrite-Host \" Online Bookstore Setup Script\" -ForegroundColor Cyan\nWrite-Host \" CS 665 Database Project\" -ForegroundColor Cyan\nWrite-Host \"================================\" -ForegroundColor Cyan\nWrite-Host \"\"\n\n# Check if Node.js is installed\nWrite-Host \"Checking if Node.js is installed...\" -ForegroundColor Yellow\ntry {\n    $nodeVersion = node --version\n    Write-Host \"Node.js is installed: $nodeVersion\" -ForegroundColor Green\n} catch {\n    Write-Host \"ERROR: Node.js is not installed. Please install Node.js first.\" -ForegroundColor Red\n    Write-Host \"Download from: https://nodejs.org/\" -ForegroundColor Yellow\n    Read-Host \"Press Enter to exit\"\n    exit 1\n}\n\n# Check if PostgreSQL is available\nWrite-Host \"Checking if PostgreSQL is available...\" -ForegroundColor Yellow\ntry {\n    $psqlVersion = psql --version\n    Write-Host \"PostgreSQL is available: $psqlVersion\" -ForegroundColor Green\n} catch {\n    Write-Host \"WARNING: PostgreSQL is not in PATH. Make sure PostgreSQL is installed.\" -ForegroundColor Yellow\n    Write-Host \"You will need to set up the database manually.\" -ForegroundColor Yellow\n}\n\nWrite-Host \"\"\nWrite-Host \"Installing dependencies...\" -ForegroundColor Yellow\n\n# Install backend dependencies\nWrite-Host \"Installing backend dependencies...\" -ForegroundColor Cyan\nSet-Location backend\ntry {\n    npm install\n    Write-Host \"Backend dependencies installed successfully.\" -ForegroundColor Green\n} catch {\n    Write-Host \"ERROR: Failed to install backend dependencies.\" -ForegroundColor Red\n    Read-Host \"Press Enter to exit\"\n    exit 1\n}\n\n# Install frontend dependencies\nWrite-Host \"Installing frontend dependencies...\" -ForegroundColor Cyan\nSet-Location ..\nSet-Location frontend\ntry {\n    npm install\n    Write-Host \"Frontend dependencies installed successfully.\" -ForegroundColor Green\n} catch {\n    Write-Host \"ERROR: Failed to install frontend dependencies.\" -ForegroundColor Red\n    Read-Host \"Press Enter to exit\"\n    exit 1\n}\n\nSet-Location ..\n\n# Set up environment files\nWrite-Host \"Setting up environment files...\" -ForegroundColor Yellow\n\nif (!(Test-Path \"backend\\.env\")) {\n    Copy-Item \"backend\\.env.example\" \"backend\\.env\"\n    Write-Host \"Created backend/.env (please update with your database credentials)\" -ForegroundColor Green\n} else {\n    Write-Host \"backend/.env already exists\" -ForegroundColor Yellow\n}\n\nif (!(Test-Path \"frontend\\.env\")) {\n    Copy-Item \"frontend\\.env.example\" \"frontend\\.env\"\n    Write-Host \"Created frontend/.env\" -ForegroundColor Green\n} else {\n    Write-Host \"frontend/.env already exists\" -ForegroundColor Yellow\n}\n\nWrite-Host \"\"\nWrite-Host \"================================\" -ForegroundColor Green\nWrite-Host \" Setup Complete!\" -ForegroundColor Green\nWrite-Host \"================================\" -ForegroundColor Green\nWrite-Host \"\"\nWrite-Host \"Next steps:\" -ForegroundColor Cyan\nWrite-Host \"1. Create PostgreSQL database: CREATE DATABASE bookstore_db;\" -ForegroundColor White\nWrite-Host \"2. Run schema: psql -U your_username -d bookstore_db -f schema.sql\" -ForegroundColor White\nWrite-Host \"3. Update backend/.env with your database credentials\" -ForegroundColor White\nWrite-Host \"4. Start backend: cd backend && npm start\" -ForegroundColor White\nWrite-Host \"5. Start frontend: cd frontend && npm start\" -ForegroundColor White\nWrite-Host \"\"\nWrite-Host \"Or use the convenience scripts:\" -ForegroundColor Cyan\nWrite-Host \"- npm run dev:backend (start backend only)\" -ForegroundColor White\nWrite-Host \"- npm run dev:frontend (start frontend only)\" -ForegroundColor White\nWrite-Host \"\"\nWrite-Host \"Test accounts:\" -ForegroundColor Cyan\nWrite-Host \"Admin login: admin@bookstore.com / admin123\" -ForegroundColor Yellow\nWrite-Host \"Customer login: john.doe@email.com / password123\" -ForegroundColor Yellow\nWrite-Host \"\"\nRead-Host \"Press Enter to exit\""